// Core PDF generation with dynamic theming
class PDFGenerator {
  private doc: jsPDF;
  private theme: PDFTheme;
  private currentY = 0;

  constructor(config: PDFConfig) {
    this.theme = config.theme;
    this.doc = new jsPDF({ orientation: 'portrait', unit: 'mm', format: 'a4' });
  }

  private applyTheme() {
    this.doc.setFillColor(this.theme.background);
    this.doc.rect(0, 0, this.doc.internal.pageSize.width, this.doc.internal.pageSize.height, 'F');
    this.doc.setTextColor(this.theme.text);
  }

  private addSection(section: SectionConfig) {
    // Add section header with theme styling
    this.doc.setFillColor(this.theme.primary);
    this.doc.roundedRect(20, this.currentY, 170, 8, 2, 'F');

    this.doc.setTextColor('#FFFFFF');
    this.doc.setFontSize(14);
    this.doc.text(section.title, 24, this.currentY + 5.5);

    this.currentY += 12;
    this.addSectionContent(section);
  }

  private addSectionContent(section: SectionConfig) {
    this.doc.setTextColor(this.theme.text);
    this.doc.setFontSize(10);

    const lines = this.doc.splitTextToSize(section.content, 170);
    lines.forEach((line: string) => {
      this.doc.text(line, 24, this.currentY);
      this.currentY += 5;
    });
    this.currentY += 8;
  }

  public async generate(): Promise<Blob> {
    this.applyTheme();
    this.config.sections.forEach(section => this.addSection(section));
    return this.doc.output('blob');
  }
}

