// Core TypeScript utility types and form validation
type ApiResponse<T, E = string> = {
  data: T;
  error: E | null;
  status: 'success' | 'error' | 'loading';
  timestamp: number;
};

type FormField<T> = {
  value: T;
  error: string | null;
  touched: boolean;
  validators: Validator<T>[];
};

// Generic form hook with type safety
function useForm<T extends Record<string, any>>(
  initialValues: T,
  validationSchema: ValidationSchema<T>
): FormState<T> {
  const [values, setValues] = useState<T>(initialValues);
  const [errors, setErrors] = useState<Partial<Record<keyof T, string>>>({});

  const setFieldValue = useCallback(<K extends keyof T>(
    field: K,
    value: T[K]
  ) => {
    setValues(prev => ({ ...prev, [field]: value }));

    // Validate field
    const validators = validationSchema[field];
    if (validators) {
      const isValid = validators.every(v => v.validate(value).isValid);
      setErrors(prev => ({
        ...prev,
        [field]: isValid ? null : 'Invalid value'
      }));
    }
  }, [validationSchema]);

  const submit = useCallback(async (): Promise<ApiResponse<T>> => {
    try {
      const response = await submitForm(values);
      return { data: response, error: null, status: 'success', timestamp: Date.now() };
    } catch (error) {
      return { data: values, error: error.message, status: 'error', timestamp: Date.now() };
    }
  }, [values]);

  return { values, errors, setFieldValue, submit, isValid: Object.values(errors).every(e => !e) };
}

