// Real-time data sync with tRPC and WebSockets
const useRealtimeData = <T>(endpoint: string, filters?: Record<string, any>) => {
  const [data, setData] = useState<T[]>([]);
  const [isConnected, setIsConnected] = useState(false);

  useEffect(() => {
    const ws = new WebSocket(`ws://localhost:3001/${endpoint}`);

    ws.onopen = () => {
      setIsConnected(true);
      ws.send(JSON.stringify({ type: 'subscribe', filters }));
    };

    ws.onmessage = (event) => {
      const { type, payload } = JSON.parse(event.data);

      switch (type) {
        case 'CREATE':
          setData(prev => [...prev, payload]);
          break;
        case 'UPDATE':
          setData(prev => prev.map(item => 
            item.id === payload.id ? { ...item, ...payload } : item
          ));
          break;
        case 'DELETE':
          setData(prev => prev.filter(item => item.id !== payload.id));
          break;
      }
    };

    return () => ws.close();
  }, [endpoint, filters]);

  return { data, isConnected };
};

