// Dynamic query builder for CRM analytics
class QueryBuilder {
  private query: any;

  constructor(table: any) {
    this.query = table;
  }

  where(field: string, operator: string, value: any) {
    this.query = this.query.where(
      sql`${sql.identifier(field)} ${sql.raw(operator)} ${value}`
    );
    return this;
  }

  orderBy(field: string, direction: 'asc' | 'desc' = 'asc') {
    this.query = this.query.orderBy(sql`${sql.identifier(field)} ${sql.raw(direction)}`);
    return this;
  }

  paginate(page: number, limit: number) {
    const offset = (page - 1) * limit;
    this.query = this.query.limit(limit).offset(offset);
    return this;
  }

  async execute() {
    return await this.query;
  }
}

