// Automated deployment pipeline
class DeploymentPipeline {
  async deploy(serviceName: string, imageTag: string) {
    console.log(`Deploying ${serviceName}:${imageTag}...`);

    // 1. Build and push image
    await this.buildImage(serviceName, imageTag);

    // 2. Update deployment
    await this.updateDeployment(serviceName, imageTag);

    // 3. Wait for rollout
    await this.waitForRollout(serviceName);

    // 4. Verify health
    await this.verifyDeployment(serviceName);

    console.log('Deployment completed successfully!');
  }

  private async buildImage(serviceName: string, tag: string) {
    const { exec } = require('child_process');
    return new Promise((resolve, reject) => {
      exec(`docker build -t ${serviceName}:${tag} .`, (error, stdout) => {
        if (error) reject(error);
        else resolve(stdout);
      });
    });
  }

  private async updateDeployment(serviceName: string, imageTag: string) {
    const deployment = await k8s.api.apps.v1.namespace('default')
      .deployments(serviceName).get();

    deployment.body.spec.template.spec.containers[0].image = `${serviceName}:${imageTag}`;

    await k8s.api.apps.v1.namespace('default')
      .deployments(serviceName).put({ body: deployment.body });
  }
}

